mapper.insert:
INSERT INTO tbl_notice
(user_number, notice_title, notice_date, notice_content, notice_count) 
VALUES (#{userNumber},#{noticeTitle},#{noticeDate},#{noticeContent},#{noticeCount});

mapper.update:
